INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 2,'S1.I2','Superficie de las áreas marinas protegidas','Un área protegida se refiere al área terrestre o marina especialmente dedicada a la protección y mantenimiento de la diversidad biológica, así como de los recursos naturales y culturales asociados, administradas de acuerdo con criterios legales u otro medio eficaz. ','NU
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 3,'S1.I3','Superficie de las áreas terrestres protegidas','Un área protegida se refiere al área terrestre o marina especialmente dedicada a la protección y mantenimiento de la diversidad biológica, así como de los recursos naturales y culturales asociados, administradas de acuerdo con criterios legales u otro medio eficaz. ','NU
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 4,'S1.I4','Consumo de sustancias agotadoras de la capa de ozono (SAO)',' Consumo de sustancias que agotan la capa de ozono. Principalmente, sobre el consumo de los tres compuestos químicos más importantes que contribuyen a la destrucción de la capa de ozono, específicamente, clorofluorocarbonos (CFC), hidroclorofluorocarbonos, (HCFC) y bromuro de metilo (metilbromuro). Además se presenta el consumo total que incluye todas las sustancias controladas por el Protocolo de Montreal. Los clorofluorocarbonos (CFCs) son productos químicos inertes, no tóxicos, que se licuan fácilmente. Se emplean en los sistemas de refrigeración, aire acondicionado, envasado y aislamiento o como solventes y  propulsores de aerosoles. Dado que los CFCs no se destruyen en las capas inferiores de la atmósfera se desplazan hacia las capas superiores, donde sus componentes clorados destruyen el ozono. En el protocolo de Montreal, los CFCs corresponden al grupo I del Anexo A, en el que se identifican las siguientes sustancias controladas: CFC-11, CFC-12, CFC-113.
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 5,'S2.I5','Tasa de desempleo','Tasa de desempleo tomado el 1er trimestre de cada año de cada paíssobre la base de la información de los países ','CEPAL','Tasa','A',2);
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 6,'S2.I6','Contribuciones sociales ','Las contribuciones sociales son ingresos provenientes de empleadores, a nombre de sus empleados, o de empleados, trabajadores por cuenta propia o no empleados, que garantizan el derecho a prestaciones sociales a los contribuyentes, sus dependientes o sus supervivientes. Estas contribuciones pueden ser obligatorias o voluntarias y se clasifican en contribuciones a la seguridad social y otras contribuciones sociales, según el tipo de sistema que las reciba. ','Información Países',' (En porcentajes del producto interno bruto)','A',2);
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 7,'S3.I7','Ingresos Tributarios  ','Ingresos tributarios: se definen como contribuciones obligatorias, sin contraprestación, no recuperables, impuestas por el gobierno con fines públicos. Se incluyen en esta categoría las contribuciones al seguro social (pagos obligatorios que efectúanlos asegurados, o sus empleadores, a instituciones gubernamentales que proporcionan prestaciones de bienestar y seguro social. Ingresos tributarios directos: 1.1. Impuestos sobre el ingreso, las utilidades y las ganancias de capital. 1.2. Impuestos sobre la propiedad. 1.3. Otros impuestos directos. 2. Ingresos tributarios indirectos: 2.1. Impuestos generales sobre bienes y servicios. 2.2. Impuestos específicos sobre bienes y servicios. 2.3. Impuestos sobre el comercio y transacciones internacionales. 2.4. Otros impuestos indirectos.
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 8,'S4.I8','Sueldos y Salarios','Sueldos y salarios: esta categoría incluyen los pagos en efectivo a las empleados a cambio de los servicios prestados y las contribuciones del gobierno en su calidad de empleador','Información Países',' (En porcentajes del producto interno bruto)','A',4);
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 9,'S5.I9','Indice de percepciones de corrupción','(Promedio. Escala de 0 a 100, donde 0 = mayor nivel de corrupción y 100 = menor nivel de corrupción)','Transparency International','Promedio','A',5);
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 10,'S5.I10','Percepciones de desigualdad y conflicto','Proporción de personas que opinan que la distribución del ingreso en su país es injusta','CEPAL','Porcentaje','A',5);
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 11,'S6.I11','Homicidio intencional','Los homicidios intencionales son cálculos de homicidios ilegales cometidos en forma intencional como resultado de conflictos internos, violencia interpersonal, conflictos violentos por la tierra o sus recursos, violencia entre pandillas por el territorio o el control y asesinatos y violencia que atenta contra la integridad física por parte de grupos armados. El homicidio intencional no incluye todos los asesinatos intencionales; la diferencia suele encontrarse en la organización del asesinato','Banco Mundial/Información de Países','Tasa por cada 100 000 ','A',6);
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 12,'S6.I12','Delitos sexuales contra niños',' ""todo delito de carácter sexual cometido contra niños [de acuerdo con la edad definida por cada país . Se deben incluyen  delitos de pornografía infantil, proxenetismo de menores, relaciones sexuales con una persona que no ha alcanzado la edad de libre consentimiento y otros delitos relacionados con la explotación sexual de menores"". 
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 13,'S6.I13','Personas Imputadas','" se entiende el número total de presuntos infractores contra quienes en el año objeto de examen se inició una acción penal por el Ministerio Público o el organismo de aplicación de la ley competente a nivel nacional, independientemente de la decisión con que se cerró la causa','Ministerios de Justicia Países','Tasa por cada 100 000 ','A',6);
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 14,'S7.I14','confianza en las instituciones políticas y del estado','Se calcula en tres etapas: 1.Se obtiene, para cada persona, el promedio simple de sus respuestas a las preguntas sobre el nivel de confianza en los partidos políticos, el poder legislativo y el poder judicial. 2. Dado que las respuestas para cada una de las instituciones se efectúan en una escala con los siguientes valores: 1) Mucha, 2) Alguna, 3) Poca y 4) Ninguna, se considera que las personas que obtienen promedios simples iguales o mayores a 3, confían poco o nada en las instituciones políticas y del estado. 3. División del total de personas de 18 años y más que confía poco o nada en las instituciones políticas y del estado (numerador) por el total de la población de 18 años y más (denominador). El resultado se multiplica por 100. ','CEPAL','Porcentaje','A',7);
