INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 17,'S9.I17','Gasto público en educación','Gasto Público en educación como porcentaje del producto nacional bruto: Se calcula dividiendo el gasto público de gobierno en un año financiero determinado (numerador) por el producto nacional bruto total de un país determinado y para ese mismo año (denominador). El resultado se multiplica por 100. Gasto Público en educación como porcentaje del producto interno bruto: Se calcula dividiendo el gasto público de gobierno en un año financiero determinado (numerador) por el producto interno bruto total de un país determinado y para ese mismo año (denominador). El resultado se multiplica por 100.','UNESCO','Porcentaje','A',9);
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 18,'S9.I18','Gasto público en salud','Se calcula como el cociente entre el gasto en salud del Gobierno General (numerador), dividido por el monto del producto interno (denominador) multiplicado por 100','OMS','Porcentaje','A',9);
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 19,'S9.I19','Hogares con acceso a internet','El porcentaje de hogares que tienen acceso a Internet es una medida del nivel de disponibilidad de esta tecnología en el total de los hogares de un país. Esta medida indica por cada 100 hogares, cuantos disponen de servicio de Internet.','CEPAL','Porcentaje','A',9);
INSERT INTO INDICADOR (CODIGO_IN, COD_GENERADO_IN, NOMBRE_IN, DEFINICION_IN, FUENTE_IN, UNIDAD_MEDIDA_IN, ESTADO_IN, CODIGO_SEC) VALUES ( 20,'S10.I20','Gasto del Gobierno Central en el Sector Agropecuario','La agricultura corresponde a las divisiones 1-5 de la CIIU e incluye la silvicultura, la caza y la pesca, además del cultivo de cosechas y la cría de animales. El valor agregado es la producción neta de un sector después de sumar todos los productos y restar los insumos intermedios. Se calcula sin hacer deducciones por depreciación de bienes manufacturados o por agotamiento y degradación de recursos naturales. El origen del valor agregado de determina a partir de la CIIU, Revisión 3. Nota: Para los países que contabilizan en base valor agregado, se utiliza como denominador el valor agregado bruto al costo de los factores','SIAGRO','Porcentaje','A',10);
